pragma solidity ^0.5.7;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Permitable.sol";

contract Market {
    uint256 public lastClearedOfferIndex;
    uint256 public lastTimeOutedOffer;
    Offer[] public offers;
    uint256 public constant OFFER_TIMEOUT = 24 * 3600;
    bytes32 public constant PERMIT_TYPEHASH = 0xea2aa0a1be11a07ed86d755c93467f4f82362b452371d1ba94d1715123511acb;
    address public timeOutBeneficiary;
    address private tempSender;
    mapping(address => uint256) nonces;
    IERC20 public daiToken;
    Permitable public dai;
    bytes32 public DOMAIN_SEPARATOR;

    constructor(address _relayerHub, address _dai, address _bfv) public {
        daiToken = IERC20(_dai);
        dai = Permitable(_dai);
        DOMAIN_SEPARATOR = Permitable(_dai).DOMAIN_SEPARATOR();

    }

    enum OfferState {
        NONE,
        SUGGESTED,
        COLLATERALIZED,
        FILLED,
        ACCEPTED,
        TIMEOUTED
    }

    struct Offer {
        string destCurrency;
        address creator;
        address taker;
        uint256 sumIn;
        uint256 sumOut;
        uint256 totalDeposited;
        uint32 timeStarted;
        OfferState status;
    }

    modifier onlySelf {
        require(msg.sender == address(this), "can be called only b self");
        _;
    }

    function getSender() public view returns (address) {
        return tempSender;
    }

    function permit(uint8 v, bytes32 r, bytes32 s) public onlySelf {
        uint8 v2 = v;
        if (v2 < 27) {
            v2 = v2 + 27;
        }
        dai.permit(
            getSender(),
            address(this),
            dai.nonces(getSender()),
            0,
            true,
            v2,
            r,
            s
        );
    }

    function relayCall(
        bytes calldata data,
        uint256 nonce,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external {
        uint8 v2 = v;
        if (v2 < 27) {
            v2 = v2 + 27;
        }
        tempSender = ecrecover(
            keccak256(abi.encodePacked(nonce, data)),
            v,
            r,
            s
        );
        require(nonces[tempSender] == nonce, "nonce invalid");
        nonces[tempSender] = nonces[tempSender] + 1;
        bool status;
        bytes memory message;
        (status, message) = address(this).call(data);
        require(status, string(message));
        tempSender = address(0);
    }

    function generateDigest(address holder) public view returns (bytes32) {
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(
                    abi.encode(
                        PERMIT_TYPEHASH,
                        holder,
                        address(this),
                        dai.nonces(holder),
                        0,
                        true
                    )
                )
            )
        );
        return digest;
    }

    function generateMethodCallDigest(bytes memory data, address user)
        public
        view
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(nonces[user], data));
    }

    function addOffer(
        uint256 sourceAmount,
        uint256 destAmount,
        string memory destCurrency
    ) public onlySelf returns (uint256 offerId) {
        offers[offers.length - 1] = Offer(
            destCurrency,
            getSender(),
            address(0),
            sourceAmount,
            destAmount,
            0,
            uint32(now),
            OfferState.SUGGESTED
        );
        daiToken.transferFrom(getSender(), address(this), 2 * sourceAmount);
        offers[offerId].totalDeposited = sourceAmount;
    }

    function canMove(address sender, uint256 offerId, uint8 destinationStateInt)
        public
        view
        returns (bool result, string memory reason)
    {
        result = true;
        reason = "All cool";
        if (destinationStateInt > 5) {
            reason = "no such state";
            result = false;
            return (result, reason);
        }
        OfferState destinationState = OfferState(destinationStateInt);
        if (offerId >= offers.length) {
            result = false;
            reason = "incorrect offer Id";
            return (result, reason);
        }
        OfferState state = offers[offerId].status;
        if (state == OfferState.NONE || state == OfferState.TIMEOUTED) {
            reason = "Offer finalized";
            result = false;
            return (result, reason);
        }
        if (state == OfferState.SUGGESTED) {
            result = false;
            reason = "transition invalid";
            if (
                destinationState == OfferState.COLLATERALIZED &&
                sender != offers[offerId].creator
            ) {
                result = true;
                reason = "All cool";
            }
            if (
                destinationState == OfferState.NONE &&
                sender == offers[offerId].creator
            ) {
                result = true;
                reason = "All cool";
            }
        }
        if (state == OfferState.COLLATERALIZED) {
            if (sender == offers[offerId].taker) {} else {
                result = false;
                reason = "person invalid COLLATERALIZED";
            }
        }
        if (state == OfferState.FILLED) {
            if (sender == offers[offerId].creator) {} else {
                result = false;
                reason = "person invalid FILLED";
            }
        }
        if (state == OfferState.ACCEPTED) {
            if (
                sender == offers[offerId].creator ||
                sender == offers[offerId].taker
            ) {} else {
                result = false;
                reason = "person invalid ACCEPTED";
            }
        }

        return (result, reason);
    }

    function nextMove(uint256 offerId, address sender)
        public
        view
        returns (string memory message, uint8 step)
    {
        OfferState state = offers[offerId].status;
        message = "Offer not available";
        step = 255;
        if (state == OfferState.SUGGESTED) {
            if (sender == offers[offerId].creator) {
                message = "Do You want to cancel this offer?";
                step = uint8(OfferState.NONE);
            } else {
                message = "Put security deposit (DAI) in order to take that order";
                step = uint8(OfferState.COLLATERALIZED);
            }
        }
        if (state == OfferState.COLLATERALIZED) {
            if (sender == offers[offerId].taker) {
                message = "Prepare cash and describe meeting point to your client";
                step = uint8(OfferState.FILLED);
            } else if (sender == offers[offerId].creator) {
                message = "Wait for meeting point instructions";
            }
        }
        if (state == OfferState.FILLED) {
            if (sender == offers[offerId].creator) {
                message = "If You have got your cash, accept";
            }
            step = uint8(OfferState.ACCEPTED);
        }
        if (state == OfferState.ACCEPTED) {
            if (sender == offers[offerId].taker) {
                message = "Congratulation, your deposit and payment should be in your wallet";
            } else if (sender == offers[offerId].creator) {
                message = "Congratulation, your deposit should be back in your wallet and cash in your pocket";
            }
            step = uint8(OfferState.NONE);
        }
        if (offers[offerId].timeStarted + OFFER_TIMEOUT > now) {
            if (
                sender == offers[offerId].taker ||
                sender == offers[offerId].creator
            ) {
                message = "Unfortunatelly, you did not manage to finalize order in time, click Proceed to send all your payments and deposits will be send to BitcoinsForVenezuela";
                step = uint8(OfferState.TIMEOUTED);
            } else {
                message = "Offer not available";
                step = 255;
            }
        }
        if (offers[offerId].timeStarted + 2 * OFFER_TIMEOUT > now) {
            message = "This Order was not successful, proceed to send all deposits to BitcoinsForVenezuela";
            step = uint8(OfferState.TIMEOUTED);
        }

        return (message, step);
    }

    function move(uint256 offerId, uint8 destinationStateInt) public {
        OfferState destinationState = OfferState(destinationStateInt);
        OfferState state = offers[offerId].status;
        if (destinationState == OfferState.COLLATERALIZED) {
            emit ProcessOffer(
                offers[offerId].taker,
                offers[offerId].creator,
                offerId,
                destinationStateInt,
                uint8(state)
            );
            processToCollateralized(state, offerId);
        }
        if (destinationState == OfferState.FILLED) {
            emit ProcessOffer(
                offers[offerId].taker,
                offers[offerId].creator,
                offerId,
                destinationStateInt,
                uint8(state)
            );
            processToFilled(state, offerId);
        }
        if (destinationState == OfferState.ACCEPTED) {
            emit ProcessOffer(
                offers[offerId].taker,
                offers[offerId].creator,
                offerId,
                destinationStateInt,
                uint8(state)
            );
            processToAccepted(state, offerId);
        }
        if (destinationState == OfferState.TIMEOUTED) {
            emit ProcessOffer(
                offers[offerId].taker,
                offers[offerId].creator,
                offerId,
                destinationStateInt,
                uint8(state)
            );
            processToTimeouted(state, offerId);
        }
        if (destinationState == OfferState.NONE) {
            emit ProcessOffer(
                offers[offerId].taker,
                offers[offerId].creator,
                offerId,
                destinationStateInt,
                uint8(state)
            );
            processToNone(state, offerId);
        }
        offers[offerId].status = destinationState;

    }

    function processToNone(OfferState state, uint256 offerId) private {
        require(state == OfferState.SUGGESTED, "Transition to NONE invalid");
        require(
            getSender() == offers[offerId].creator,
            "Only offer crator can cancel offer"
        );
        daiToken.transferFrom(
            address(this),
            offers[offerId].creator,
            offers[offerId].totalDeposited
        );
        offers[offerId].totalDeposited = 0;
    }

    function processToTimeouted(OfferState state, uint256 offerId) private {
        require(
            state != OfferState.SUGGESTED && state != OfferState.NONE,
            "Transition to TIMEOUT invalid"
        );
        require(
            offers[offerId].timeStarted + OFFER_TIMEOUT > block.timestamp,
            "Time not passed yet"
        );
        daiToken.transferFrom(
            address(this),
            timeOutBeneficiary,
            offers[offerId].totalDeposited
        );
        offers[offerId].totalDeposited = 0;
    }

    function processToAccepted(OfferState state, uint256 offerId) private {
        require(state == OfferState.FILLED, "Transition to ACCEPTED invalid");
        require(
            getSender() == offers[offerId].creator,
            "Only DAI Sender (creator) can accept order"
        );
        require(
            offers[offerId].totalDeposited >= offers[offerId].sumIn * 3,
            "Payouts bigger than deposits"
        );
        daiToken.transferFrom(
            address(this),
            offers[offerId].creator,
            offers[offerId].sumIn
        );
        daiToken.transferFrom(
            address(this),
            offers[offerId].taker,
            offers[offerId].sumIn * 2
        );
    }

    function processToFilled(OfferState state, uint256 offerId) private {
        require(
            state == OfferState.COLLATERALIZED,
            "Transition to FILLED invalid"
        );
        require(
            getSender() == offers[offerId].taker,
            "Only DAI Recipient (creator) can fill order"
        );

    }

    function processToCollateralized(OfferState state, uint256 offerId)
        private
    {
        if (now > offers[offerId].timeStarted) {
            require(
                offers[offerId].taker == address(0),
                "taker must not be set"
            );
            require(
                offers[offerId].creator != getSender(),
                "taker cannot equal creator"
            );
            daiToken.transferFrom(
                getSender(),
                address(this),
                offers[offerId].sumIn
            );

            offers[offerId].totalDeposited =
                offers[offerId].totalDeposited +
                offers[offerId].sumIn;

            offers[offerId].taker = getSender();
        } else {
            revert("Transition to COLLATERALIZED invalid");
        }
    }

    function getLastOfferIndex() public view returns (int256) {
        return (int256(offers.length)) - 1;
    }

    function houseKeeping() public {
        uint256 i = lastTimeOutedOffer - 1;
        while (gasleft() > 200000 && i < offers.length) {
            if (offers[i].timeStarted + OFFER_TIMEOUT > now) {
                offers[i].status = OfferState.TIMEOUTED;
            }

            i = i + 1;
        }
        lastTimeOutedOffer = i;
    }

    event ProcessOffer(
        address indexed taker,
        address indexed creator,
        uint256 offerId,
        uint8 destinationStatus,
        uint8 sourceStatus
    );
}
